void display_processing(uint8_t *data, uint8_t length) {
    uint8_t data_type = data[0];
    
    switch (data_type) {
        case 248:
            memcpy(timeBuffer, &data[1], 9);    
            printf("Print: %s \n", timeBuffer);
            break;

        case 249:
            snprintf(VolumeBuffer, sizeof(VolumeBuffer), "%d", data[1]);
            printf("Print: %s \n", VolumeBuffer);
            break;

        case 251:
            memcpy(MediaArtistBuffer, &data[2], data[1]);
            printf("Title: %s \n", MediaArtistBuffer);
            break;
        
        case 252:
            memcpy(MediaTitleBuffer, &data[2], data[1]);
            printf("Title: %s \n", MediaTitleBuffer);
            break;

        case 253:
            render_anim();
            break;

        default:
            break;
    }
}

